<?php require_once('database.php'); session_start(); $username = $_SESSION['username']; $password = $_SESSION['password']; $conn = dbConnection(); $query = "SELECT * FROM userinfo WHERE username='$username' AND password='$password'"; $result = mysqli_query($conn, $query); if (mysqli_num_rows($result) == 1) { $row = mysqli_fetch_assoc($result); $_SESSION['username'] = $row['username']; $_SESSION['role'] = $row['role']; if ($row['role'] === 'Client') { header('Location: client.php'); } elseif ($row['role'] === 'Admin') { header('Location: admin.php'); } } ?>